import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import '../../styles/communityStyles/boardWrite.css';
import axios from 'axios';

const BoardWrite: React.FC = () => {
    const { category } = useParams<{ category: string }>();
    const [title, setTitle] = useState('');
    const [name, setName] = useState('');
    const [content, setContent] = useState('');

    // ...

    const handleTitleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        setTitle(e.target.value);
    };

    const handleNameChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        setName(e.target.value);
    };

    const handleContentChange = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
        setContent(e.target.value);
    };

    const handleFormSubmit = (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();

        // 게시글 데이터 생성
        const postData = {
            title: title,
            name: name,
            content: content
        };

        // 임시 이름 생성
        const tempName = name || 'Anonymous';

        new Promise((resolve, reject) => {
            axios.post(`/write/${category}`, { ...postData, name: tempName })
                .then(response => {
                    console.log('게시글이 성공적으로 등록되었습니다.');
                    // 게시글 등록 후에 필요한 동작 수행
                    // 예: 등록 성공 시 페이지 이동 등
                    resolve(response.data); // 성공적으로 등록되었을 경우 Promise를 성공 상태로 전환
                })
                .catch(error => {
                    console.log('게시글 등록에 실패했습니다:', error);
                    reject(error); // 등록 실패 시 Promise를 실패 상태로 전환
                });
        });
    };

    return (
        <section className="writeOutterWrap">
            <div className="wirteDecoTop"></div>

            <article className="writeInnerWrap">
                <div className="writeBoxTop">
                    <h3>{category} 글쓰기</h3>
                    <form onSubmit={handleFormSubmit}>
                        {/* 글 제목 */}
                        <div className="inputGroup">
                            <label htmlFor="title">글 제목</label>
                            <input
                                type="text"
                                id="title"
                                value={title}
                                onChange={handleTitleChange}
                            />
                        </div>

                        {/* 임시 아이디 */}
                        <div className="inputGroup">
                            <label htmlFor="name">임시 아이디</label>
                            <input
                                type="text"
                                id="name"
                                value={name}
                                onChange={handleNameChange}
                            />
                        </div>

                        {/* 본문 작성 */}
                        <div className="inputGroup">
                            <label htmlFor="content">본문 작성</label>
                            <textarea
                                id="content"
                                value={content}
                                onChange={handleContentChange}
                            ></textarea>
                        </div>

                        {/* 버튼 그룹 */}
                        <div className="buttonGroup">
                            <button type="submit">등록</button>
                            <button type="button">취소</button>
                        </div>
                    </form>
                </div>
            </article>

            <div className="wirteDecoBottom"></div>
        </section>
    );
};

export default BoardWrite;
