import React, { useEffect, useState } from 'react';
import { CommentOne } from '../../../types';

interface ReplyItemProps {
    comment: CommentOne;
    fetchReplies: () => void;
}

const ReplyItem: React.FC<ReplyItemProps> = ({ comment, fetchReplies }) => {
    const [replyList, setReplyList] = useState<CommentOne[]>([]);
    const [isReplyVisible, setIsReplyVisible] = useState(false);

    const handleReply = () => {
        if (isReplyVisible) {
            setIsReplyVisible(false); // 이미 보이는 상태에서 다시 버튼을 클릭하면 숨김 처리
        } else {
            fetchReplies(); // 대댓글 가져오기
            setIsReplyVisible(true); // 대댓글 표시
        }
    };

    const handleEdit = () => {
        // 수정 버튼 클릭 시 실행될 동작을 구현합니다.
    };

    const handleDelete = () => {
        // 삭제 버튼 클릭 시 실행될 동작을 구현합니다.
    };

    return (
        <li className="replyOne" key={comment.id}>
            <p>{comment.name}</p>
            <p>{comment.createdate}</p>
            <p>{comment.content}</p>
            <div className="replyButtons">
                <div>
                    <span>{comment.like_count}</span>
                    <span>{comment.dislike_count}</span>
                    <span className="replyButton" onClick={handleReply}>
            답글
          </span>
                </div>
                <div>
          <span className="editButton" onClick={handleEdit}>
            수정
          </span>
                    <span className="deleteButton" onClick={handleDelete}>
            삭제
          </span>
                </div>
            </div>
            <span className="replyCountButton" onClick={handleReply}>
        {isReplyVisible ? '답글 숨기기' : `답글 ${replyList.length}개`}
      </span>
            {isReplyVisible && (
                <ul>
                    {replyList.map((reply) => (
                        <li key={reply.id}>{reply.content}</li>
                    ))}
                </ul>
            )}
        </li>
    );
};

export default ReplyItem;
