import React, { useState, useEffect } from 'react';
import { useParams, useLocation } from 'react-router-dom';
import '../../styles/communityStyles/boardWrite.css';
import axios from 'axios';

const BoardWrite: React.FC = () => {
    const { category } = useParams<{ category: string }>();
    const location = useLocation();
    const searchParams = new URLSearchParams(location.search);
    const editMode = searchParams.get('edit') === 'true';
    const postId = searchParams.get('id');
    const [title, setTitle] = useState('');
    const [name, setName] = useState('');
    const [content, setContent] = useState('');

    useEffect(() => {
        if (editMode && postId) {
            axios
                .get(`/boarddetail/${category}/${postId}`)
                .then((response) => {
                    const postingDetail = response.data;

                    setTitle(postingDetail.title);
                    setName(postingDetail.name);
                    setContent(postingDetail.content);
                })
                .catch((error) => {
                    console.log('게시글을 가져오는 데 실패했습니다:', error);
                });
        }
    }, [category, editMode, postId]);

    const handleTitleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        setTitle(e.target.value);
    };

    const handleNameChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        setName(e.target.value);
    };

    const handleContentChange = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
        setContent(e.target.value);
    };

    const postData = {
        title: title,
        name: name,
        content: content,
    };

    const handleFormSubmit = (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();

        if (editMode && postId) {
            // 수정 모드인 경우 게시글 업데이트 로직 수행
            axios
                .put(`/update/${category}/${postId}`, postData)
                .then((response) => {
                    console.log('게시글이 성공적으로 업데이트되었습니다.');
                    // 게시글 업데이트 후에 필요한 동작 수행
                    // 예: 업데이트 성공 시 페이지 이동 등
                })
                .catch((error) => {
                    console.log('게시글 업데이트에 실패했습니다:', error);
                });
        } else {
            // 새로운 게시글 등록 로직 수행
            axios
                .post(`/write/${category}`, postData)
                .then((response) => {
                    console.log('게시글이 성공적으로 등록되었습니다.');
                    // 게시글 등록 후에 필요한 동작 수행
                    // 예: 등록 성공 시 페이지 이동 등
                })
                .catch((error) => {
                    console.log('게시글 등록에 실패했습니다:', error);
                });
        }
    };

    return (
        <section className="writeOutterWrap">
            <div className="wirteDecoTop"></div>

            <article className="writeInnerWrap">
                <div className="writeBoxTop">
                    <h3>{category} 글쓰기</h3>
                    <form onSubmit={handleFormSubmit}>
                        {/* 글 제목 */}
                        <div className="inputGroup">
                            <label htmlFor="title">글 제목</label>
                            <input
                                type="text"
                                id="title"
                                value={title}
                                onChange={handleTitleChange}
                            />
                        </div>

                        {/* 임시 아이디 */}
                        <div className="inputGroup">
                            <label htmlFor="name">임시 아이디</label>
                            <input
                                type="text"
                                id="name"
                                value={name}
                                onChange={handleNameChange}
                            />
                        </div>

                        {/* 본문 작성 */}
                        <div className="inputGroup">
                            <label htmlFor="content">본문 작성</label>
                            <textarea
                                id="content"
                                value={content}
                                onChange={handleContentChange}
                            ></textarea>
                        </div>

                        {/* 버튼 그룹 */}
                        <div className="buttonGroup">
                            <button type="submit">
                                {editMode ? '수정' : '등록'}
                            </button>
                            <button type="button">취소</button>
                        </div>
                    </form>
                </div>
            </article>

            <div className="wirteDecoBottom"></div>
        </section>
    );
};

export default BoardWrite;
