    };

    const handleDelete = () => {
        // 삭제 버튼 클릭 시 실행할 동작
    };

    return (
        <li className="replyOne" key={comment.id}>
            <p>{comment.name}</p>
            <p>{comment.createdate}</p>
            <p>{comment.content}</p>
            <div className="replyButtons">
                <div>
                    <span>{comment.like_count}</span>
                    <span>{comment.dislike_count}</span>
                    <span className="replyButton" onClick={handleReply}>
                        답글
                    </span>
                </div>
                <div>
                    <span className="editButton" onClick={handleEdit}>
                        수정
                    </span>
                    <span className="deleteButton" onClick={handleDelete}>
                        삭제
                    </span>
                </div>
            </div>
            <span className="replyCountButton" onClick={handleReply}>
                답글 N개
            </span>
        </li>
    );
};

export default ReplyItem;
