import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Posting } from '../../types';
import '../../styles/communityStyles/boardDetail.css';

const EditDeleteButtons: React.FC<{ handleEdit: () => void; handleDelete: () => void }> = ({
                                                                                               handleEdit,
                                                                                               handleDelete,
                                                                                           }) => {
    return (
        <div>
            <button type="button" onClick={handleEdit}>
                수정
            </button>
            <button type="button" onClick={handleDelete}>
                삭제
            </button>
        </div>
    );
};

const BoardDetail: React.FC = () => {
    const { id, category } = useParams<{ id: string; category: string }>();
    const [posting, setPosting] = useState<Posting>();
    const navigate = useNavigate();

    useEffect(() => {
        axios
            .get(`/boarddetail/${category}/${id}`)
            .then((response) => setPosting(response.data))
            .catch((error) => {
                console.error('게시물을 가져오는 데 실패했습니다.', error);
            });
    }, [id]);

    const handleEdit = () => {
        // 게시물 수정 로직
        navigate(`/writepost/${category}?edit=true&id=${id}`);
    };

    const handleDelete = () => {
        axios
            .delete(`/delete/${category}/${id}`)
            .then((response) => {
                alert(response.data);
                navigate(`/${category}`);
            })
            .catch((error) => {
                alert('게시글 삭제에 실패했습니다:' + error);
            });
    };

    const handleGoBack = () => {
        navigate(-1);
    };

    const handleGoList = () => {
        navigate('/${category}');
    };

    return (
        <section className="board-detail-container">

            <article className="board-detail">
                <div className="container">
                    <div className="post">
                        <div className="post-info">
                            <div className="post-header">
                                <h3>{posting?.title}</h3>
                                <p>글 번호: {posting?.id}</p>
                            </div>
                            <div className="post-details">
                                <p>
                                    작성자: {posting?.name} | 작성일: {posting?.createdate} | 조회수: {posting?.views}
                                </p>
                            </div>
                        </div>
                        <div className="post-content">
                            <p>{posting?.content}</p>
                        </div>
                    </div>
                </div>
                {true && (
                    <EditDeleteButtons handleEdit={handleEdit} handleDelete={handleDelete} />
                )}
                <button type="button" onClick={handleGoBack}>뒤로가기</button>
                <button type="button" onClick={handleGoList}>목록으로</button>
            </article>
        </section>
    );
};

export default BoardDetail;
