import React, {useEffect, useState} from 'react';
import {useParams, useNavigate} from 'react-router-dom';
import axios from 'axios';
import {CommentOne, Posting} from '../../types';
import '../../styles/communityStyles/boardDetail.css';
import ReplyInputContainer from './component/ReplyInputContainer';
import EditDeleteButtons from './component/EditDeleteButtons';

const BoardDetail: React.FC = () => {
    const {id, category} = useParams<{ id: string; category: string }>();
    const [posting, setPosting] = useState<Posting>();
    const [commentInput, setCommentInput] = useState('');
    const [commentMainInput, setCommentMainInput] = useState('');
    const [commentList, setCommentList] = useState<CommentOne[]>([]);
    const [isReplyVisible, setReplyVisible] = useState<{ [commentId: number]: boolean }>({});
    const [replyList, setReplyList] = useState<{ [commentId: number]: CommentOne[] }>({});
    const [replyInputCommentId, setReplyInputCommentId] = useState<number | null>(null);
    const [isReplyInputVisible, setReplyInputVisible] = useState(false);
    const navigate = useNavigate();

    // 게시글 및 댓글 정보 가져오기
    useEffect(() => {
        axios
            .get(`/boarddetail/${category}/${id}`)
            .then((response) => setPosting(response.data))
            .catch((error) => {
                console.error('게시물을 가져오는 데 실패했습니다.', error);
            });

        axios
            .get(`/get-comments?category=${category}&id=${id}`)
            .then((response) => setCommentList(response.data))
            .catch((error) => {
                console.error('댓글을 가져오는 데 실패했습니다.', error);
            });
    }, [id, category]);

    // 게시글 수정 페이지로 이동
    const handleEdit = () => {
        navigate(`/writepost/${category}?edit=true&id=${id}`);
    };

    // 게시글 삭제
    const handleDelete = () => {
        axios
            .delete('/delete-posting', {
                params: {
                    category: category,
                    id: id,
                },
            })
            .then((response) => {
                alert(response.data);
                navigate(`/${category}`);
            })
            .catch((error) => {
                alert('게시글 삭제에 실패했습니다:' + error);
            });
    };

    // 이전 페이지로 이동
    const handleGoBack = () => {
        navigate(-1);
    };

    // 목록 페이지로 이동
    const handleGoList = () => {
        navigate(`/${category}`);
    };

    // 댓글 입력 내용 변경
    const handleCommentInputChange = (event: React.ChangeEvent<HTMLTextAreaElement>) => {
        setCommentInput(event.target.value);
    };

    const handleCommentMainInputChange = (event: React.ChangeEvent<HTMLTextAreaElement>) => {
        setCommentMainInput(event.target.value);
    };

    // 댓글 등록
    const handleCommentSubmit = () => {
        if (commentMainInput.trim() !== '') {
            axios
                .post(`/add-comment?postid=${id}&category=${category}&content=${commentMainInput}&name="세션아이디로변경"`)
                .then((response) => {
                    setCommentMainInput('');
                    // 댓글 등록 후 댓글 목록 업데이트
                    axios
                        .get(`/get-comments?category=${category}&id=${id}`)
                        .then((response) => setCommentList(response.data))
                        .catch((error) => {
                            console.error('댓글을 가져오는 데 실패했습니다.', error);
                        });
                })
                .catch((error) => {
                    console.error('댓글 추가에 실패했습니다.', error);
                });
        } else {
            alert('댓글 내용을 입력해주세요!');
        }
    };

    const handleReCommentInput = (commentId: number) => {
        setReplyInputCommentId(commentId);
        setReplyInputVisible(true); // ReplyInputContainer를 보이도록 설정
    };

    const handleReply = (commentId: number) => {
        if (isReplyVisible[commentId]) {
            setReplyVisible((prevState) => ({
                ...prevState,
                [commentId]: false,
            }));
        } else {
            axios
                .get(`/re-reply?category=${category}&root=${id}&mention=${commentId}`)
                .then((response) => {
                    setReplyList((prevState) => ({
                        ...prevState,
                        [commentId]: response.data,
                    }));
                    setReplyVisible((prevState) => ({
                        ...prevState,
                        [commentId]: true,
                    }));
                })
                .catch((error) => {
                    console.error('대댓글을 가져오는 데 실패했습니다.', error);
                });
        }
    };

    const handleReplyCommentSubmit = (commentid:number, commentInput: string) => {
        const additionalData = {
            root: id,
            mention: commentid,
            content: commentInput,
        };
        if (commentInput.trim() !== '') {
            axios
                .post(`/add-comment?commentid=${commentid}&category=${category}&name='세션아이디넣을거임'`, additionalData)
                .then((response) => setCommentInput(''))
                .catch((error) => {
                    console.error('댓글 추가에 실패했습니다.', error);
                });
        } else {
            alert('댓글 내용을 입력해주세요!');
        }
    };

    return (
        <section className="board-detail-container">
            <div className="detailDecoTop">데코 들어갈 자리</div>

            <article className="board-detail">
                <div className="container">
                    <div className="post">
                        <div className="post-info">
                            <div className="post-header">
                                <h3>{posting?.title}</h3>
                                <p>글 번호: {posting?.id}</p>
                            </div>
                            <div className="post-details">
                                <p>
                                    작성자: {posting?.name} | 작성일: {posting?.createdate} | 조회수: {posting?.views}
                                </p>
                            </div>
                        </div>
                        <div className="post-content">
                            <p>{posting?.content}</p>
                        </div>
                    </div>
                </div>
                {true && <EditDeleteButtons handleEdit={handleEdit} handleDelete={handleDelete}/>}
                <div className="button-group">
                    <span className="button" onClick={handleGoBack}>뒤로가기</span>
                    <span className="button" onClick={handleGoList}>목록으로</span>
                </div>
            </article>
            <article className="replyContainer">
                <ReplyInputContainer
                    commentInput={commentMainInput}
                    handleCommentInputChange={handleCommentMainInputChange}
                    handleCommentSubmit={handleCommentSubmit}
                    isVisible={isReplyInputVisible}
                    setReplyInputVisible={setReplyInputVisible}
                />
                <ul className="replyList">
                    {commentList.map((comment) => (
                        <li className="replyOne" key={comment.id}>
                            <p>{comment.name}</p>
                            <p>{comment.createdate}</p>
                            <p>{comment.content}</p>
                            <div className="replyButtons">
                                <div>
                                    <span>{comment.like_count}</span>
                                    <span>{comment.dislike_count}</span>
                                    <span className="replyButton" onClick={() =>handleReCommentInput(comment.id)}>답글</span>
                                </div>
                                <div>
                                    <span className="editButton" onClick={handleEdit}>수정</span>
                                    <span className="deleteButton" onClick={handleDelete}>삭제</span>
                                </div>
                            </div>
                            <span className="replyCountButton" onClick={() => handleReply(comment.id)}>
                                {isReplyVisible[comment.id] ? '답글 숨기기' : `답글 ${comment.reply_count}개`}
                            </span>
                            {replyInputCommentId === comment.id && (
                                <ReplyInputContainer
                                    commentInput={commentInput}
                                    handleCommentInputChange={handleCommentInputChange}
                                    handleCommentSubmit={() => handleReplyCommentSubmit(comment.id, comment.content)}
                                    isVisible={isReplyInputVisible}
                                    setReplyInputVisible={setReplyInputVisible}
                                />
                            )}
                            {isReplyVisible[comment.id] && (
                                <ul>
                                    {replyList[comment.id]?.map((reply) => (
                                        <li key={'rere_' + reply.id}>
                                            {reply.content}, {reply.createdate}
                                        </li>
                                    ))}
                                </ul>
                            )}
                        </li>
                    ))}
                </ul>
            </article>

            <div className="detailDecoTop">데코 들어갈 자리</div>
        </section>
    );
};

export default BoardDetail;
