import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { CommentOne, Posting } from '../../types';
import '../../styles/communityStyles/boardDetail.css';

const EditDeleteButtons: React.FC<{ handleEdit: () => void; handleDelete: () => void }> = ({
                                                                                               handleEdit,
                                                                                               handleDelete,
                                                                                           }) => {
    return (
        <div className="button-group">
      <span className="button" onClick={handleEdit}>
        수정
      </span>
            <span className="button" onClick={handleDelete}>
        삭제
      </span>
        </div>
    );
};

const BoardDetail: React.FC = () => {
    const { id, category } = useParams<{ id: string; category: string }>();
    const [posting, setPosting] = useState<Posting>();
    const [commentInput, setCommentInput] = useState('');
    const [commentList, setCommentList] = useState<CommentOne[]>([]);
    const navigate = useNavigate();


    useEffect(() => {
        axios
            .get(`/boarddetail/${category}/${id}`)
            .then((response) => setPosting(response.data))
            .catch((error) => {
                console.error('게시물을 가져오는 데 실패했습니다.', error);
            });

        axios
            .get(`/get-comments?category=${category}&postid=${id}`)
            .then((response) => setCommentList(response.data))
            .catch((error) => {
                console.error('댓글을 가져오는 데 실패했습니다.', error);
            });
    }, [id, category]);

    const handleEdit = () => {
        navigate(`/writepost/${category}?edit=true&id=${id}`);
    };

    const handleDelete = () => {
        axios
            .delete('/delete-posting', {
                params: {
                    category: category,
                    id: id,
                },
            })
            .then((response) => {
                alert(response.data);
                navigate(`/${category}`);
            })
            .catch((error) => {
                alert('게시글 삭제에 실패했습니다:' + error);
            });
    };

    const handleGoBack = () => {
        navigate(-1);
    };

    const handleGoList = () => {
        navigate(`/${category}`);
    };

    const handleCommentInputChange = (event: React.ChangeEvent<HTMLTextAreaElement>) => {
        setCommentInput(event.target.value);
    };

    const handleCommentSubmit = () => {
        if (commentInput.trim() !== '') {
            axios
                .post(`/add-comment?postid=${id}&category=${category}&content=${commentInput}&name="세션아이디로변경"`)
                .then((response) => {
                    setCommentInput('');
                })
                .catch((error) => {
                    console.error('댓글 추가에 실패했습니다.', error);
                });
        } else {
            alert("댓글 내용을 입력해주세요!")
        }
    };

    return (
        <section className="board-detail-container">
            <div className="detailDecoTop">데코 들어갈 자리</div>

            <article className="board-detail">
                <div className="container">
                    <div className="post">
                        <div className="post-info">
                            <div className="post-header">
                                <h3>{posting?.title}</h3>
                                <p>글 번호: {posting?.id}</p>
                            </div>
                            <div className="post-details">
                                <p>
                                    작성자: {posting?.name} | 작성일: {posting?.createdate} | 조회수: {posting?.views}
                                </p>
                            </div>
                        </div>
                        <div className="post-content">
                            <p>{posting?.content}</p>
                        </div>
                    </div>
                </div>
                {true && <EditDeleteButtons handleEdit={handleEdit} handleDelete={handleDelete} />}
                <div className="button-group">
          <span className="button" onClick={handleGoBack}>
            뒤로가기
          </span>
                    <span className="button" onClick={handleGoList}>
            목록으로
          </span>
                </div>
            </article>
            <article className="replyContainer">
        <textarea
            placeholder="댓글 추가"
            value={commentInput}
            onChange={handleCommentInputChange}
        ></textarea>
                <div className="replyInput">
                    <span>취소</span>
                    <span onClick={handleCommentSubmit} className={commentInput ? "active" : ""}>등록</span>
                </div>
                <ul className="replyList">
                    {commentList.map((comment) => (
                        <li className="replyOne" key={comment.id}>
                            <p>{comment.name}</p><p>{comment.createdate}</p>
                            <p>댓글 내용댓글 내용댓글 내용댓글 내용댓글 내용댓글 내용</p>
                            <div className="replyButtons">
                                <div>
                                    <span>{comment.like_count}</span>
                                    <span>{comment.dislike_count}</span>
                                    <span>답글</span>
                                </div>
                                <div>
                                    <span>수정</span>
                                    <span>삭제</span>
                                </div>
                            </div>
                        </li>
                    ))}
                </ul>
            </article>

            <div className="detailDecoTop">데코 들어갈 자리</div>
        </section>
    );
};

export default BoardDetail;
