import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { Posting } from '../../types';
import '../../styles/communityStyles/boardDetail.css';

const BoardDetail: React.FC = () => {
    const { id } = useParams<{ id: string }>();
    const [posting, setPosting] = useState<Posting>();

    useEffect(() => {
        axios
            .get(`/boarddetail/${id}`)
            .then((response) => setPosting(response.data))
            .catch((error) => {
                console.error('게시물을 가져오는 데 실패했습니다.', error);
            });
    }, [id]);

    return (
        <main>
            <div className="board-detail-container">
                <section className="board-detail">
                    <div className="container">
                        <div className="title">{posting?.title}</div>
                        <div className="info-row">
                            <div className="info-item">
                                <span className="label">작성자:</span> {posting?.name}
                            </div>
                            <div className="info-item">
                                <span className="label">작성일:</span> {posting?.createdate}
                            </div>
                        </div>
                        <div className="content">{posting?.content}</div>
                        <div className="comment-input">댓글 입력창</div>
                        <div className="comment-list">댓글 리스트</div>
                    </div>
                </section>
            </div>
        </main>
    );
};

export default BoardDetail;
