import React from 'react';
import ReplyInputContainer from './ReplyInputContainer';

const CommentItem: React.FC<{
    comment: CommentOne;
    isReplyVisible: boolean;
    replyInputCommentId: number | null;
    commentInput: string;
    handleCommentInputChange: (event: React.ChangeEvent<HTMLTextAreaElement>) => void;
    handleReCommentInput: (commentId: number) => void;
    handleEdit: () => void;
    handleDelete: () => void;
    handleReply: (commentId: number) => void;
    handleReplyCommentSubmit: (commentId: number, commentInput: string) => void;
    isReplyInputVisible: boolean;
    setReplyInputVisible: React.Dispatch<React.SetStateAction<boolean>>;
}> = ({
          comment,
          isReplyVisible,
          replyInputCommentId,
          commentInput,
          handleCommentInputChange,
          handleReCommentInput,
          handleEdit,
          handleDelete,
          handleReply,
          handleReplyCommentSubmit,
          isReplyInputVisible,
          setReplyInputVisible,
      }) => {
    return (
        <li className="replyOne" key={comment.id}>
            <p>{comment.name}</p>
            <p>{comment.createdate}</p>
            <p>{comment.content}</p>
            <div className="replyButtons">
                <div>
                    <span>{comment.like_count}</span>
                    <span>{comment.dislike_count}</span>
                    <span className="replyButton" onClick={() => handleReCommentInput(comment.id)}>답글</span>
                </div>
                <div>
                    <span className="editButton" onClick={handleEdit}>수정</span>
                    <span className="deleteButton" onClick={handleDelete}>삭제</span>
                </div>
            </div>
            <span className="replyCountButton" onClick={() => handleReply(comment.id)}>
                {isReplyVisible[comment.id] ? '답글 숨기기' : `답글 ${comment.reply_count}개`}
            </span>
            {replyInputCommentId === comment.id && (
                <ReplyInputContainer
                    commentInput={commentInput}
                    handleCommentInputChange={handleCommentInputChange}
                    handleCommentSubmit={() => handleReplyCommentSubmit(comment.id, comment.content)}
                    isVisible={isReplyInputVisible}
                    setReplyInputVisible={setReplyInputVisible}
                />
            )}
            {isReplyVisible[comment.id] && CommentItem}
        </li>
    );
};

export default CommentItem;
